contract Vesting is Ownable {
    IERC20 public token;
    uint256 public vestingStart;
    uint256 public vestingDuration = 180 days; // 6 months
    mapping(address => uint256) public vestedAmounts;
    mapping(address => uint256) public releasedAmounts;
    constructor(address tokenAddress, address initialOwner) Ownable() {
        require(tokenAddress != address(0), "Invalid token address");
        token = IERC20(tokenAddress);
        vestingStart = block.timestamp;
     // Set the owner manually
    _transferOwnership(initialOwner);
    }
    // Add a function to release vested tokens
    function releaseTokens(address user) external {
        uint256 releasable = calculateReleasableTokens(user);
        releasedAmounts[user] += releasable;
        require(token.transfer(user, releasable), "Transfer failed");
    }
    function calculateReleasableTokens(address user) public view returns (uint256) {
        uint256 vested = vestedAmounts[user];
        uint256 timePassed = block.timestamp - vestingStart;
        uint256 vestingRate = (vested * 2) / 100;  // 2% per month
        return vestingRate * (timePassed / 30 days) - releasedAmounts[user];  // Linear release
    }
}

